function setLine(c,b){var a=(parseInt(c.random)/parseInt(c.location)*b);switch(c.signal_value){case"100_ABOVE":a=a+(b*0);break;case"90_ABOVE":a=a+(b*1);break;case"80_ABOVE":a=a+(b*2);break;case"70_ABOVE":a=a+(b*3);break;case"60_ABOVE":a=a+(b*4);break;case"50":a=a+(b*5);break;case"60_BELOW":a=a+(b*6);break;case"70_BELOW":a=a+(b*7);break;case"80_BELOW":a=a+(b*8);break;case"90_BELOW":a=a+(b*9);break;case"100_BELOW":a=a+(b*10);break}return a}function updateGraph(a,b){var c=$(".line").eq(a);$.getJSON("http://localhost:8080/php/get_data.php?t="+Date.now()).done(function(d){c.css("top",setLine(d,b));setTimeout(function(){updateGraph(a,b)},d.interval*1000)})}$(function(){h=$(".img").height()/11;updateGraph(0,h);updateGraph(1,h);updateGraph(2,h);updateGraph(3,h)});